import React, { Component } from 'react';

class Testimonials extends Component {
  render() {

    if(this.props.data){
      var testimonials = this.props.data.testimonials.map(function(testimonials){
        return  <li key={testimonials.user}>
            <blockquote>
               <p>{testimonials.text}</p>
               <cite>{testimonials.user}</cite>
            </blockquote>
         </li>
      })
    }

    return (
      <section id="testimonials">
      <div className="container" id="thesis" style="margin-top:30px">
        <div className="a4" style="height: 29.7cm">
            <h1 className="my-4">碩士論文</h1>
                <iframe  style="width: 100%;height:26cm" 
                src="https://drive.google.com/file/d/1LG6BvmlEYOerU5QPb0iQV_MOF2qt3JAU/preview">
                </iframe>
        </div>
      </div>
      </section> 
    );
  }
}

export default Testimonials;
