import React, { Component } from 'react';

class Testimonials extends Component {
  render() {

    if(this.props.data){
      var testimonials = this.props.data.testimonials.map(function(testimonials){
        return  <li key={testimonials.user}>
            <blockquote>
               <p>{testimonials.text}</p>
               <cite>{testimonials.user}</cite>
            </blockquote>
         </li>
      })
    }

  return (
    <section id="testimonials">

    <div className="container" id="thesis" styles="margin-top:30px">
        <div className="a4" styles="height: 29.7cm">
            <h1 class="my-4">碩士論文</h1>
              <div id="Iframe-Master-CC-and-Rs" className="set-margin set-padding set-border set-box-shadow center-block-horiz">
                <div className="responsive-wrapper responsive-wrapper-wxh-572x612" styles="-webkit-overflow-scrolling: touch; overflow: auto;">
                  <iframe src="https://drive.google.com/file/d/1LG6BvmlEYOerU5QPb0iQV_MOF2qt3JAU/preview"> 
                    <p styles="font-size: 110%;"><em><strong>ERROR: </strong>  
                      An &#105;frame should be displayed here but your browser version does not support &#105;frames. </em>Please update your browser to its most recent version and try again.</p>
                  </iframe>
                </div>
              </div>
          </div>
      </div>
    </section>


  );


  }
}

export default Testimonials;
